mapper.insert:
insert into commu(
	commu_no, member_no, middlename, map_no,
	title, content
)
values(
	#{commuNo}, #{memberNo},#{middlename}, #{mapNo},
	#{title}, #{content}
)

mapper.update:
update commu set
	title = #{title}, content = #{content}
where
	commu_no = #{commuNo}

update commu set
	hiddenyn = 'Y'
where commu_no = #{commuNo}

mapper.select:
select * from ( select rownum rn, TMP.* from ( select co.commu_no, co.title, co.regdate, me.nick, CIF.image_no from commu co left outer join commu_img_first CIF on co.commu_no = CIF.commu_no left join member me on co.member_no = me.member_no left join cmc cmc on co.middlename = cmc.middle_name where cmc.large_name = #{largeName} and co.hiddenyn = 'N'
and instr(${search}, #{keyword}) > 0
order by commu_no desc )TMP ) where rn between ${startRow} and ${endRow}
