mapper.insert:
insert into commu(
	commu_no, member_no, middlename, map_no,
	title, content
)
values(
	#{commuNo}, #{memberNo},#{middleName},#{mapNo},
	#{title}, #{content}
)

mapper.select:
select * from (
	select rownum rn, TMP.* from (
		select * from commu where middlename = #{middleName} order by commu_no desc
	)TMP
) where rn between #{startRow} and #{endRow}

mapper.update:
update commu set
	title = #{title}, content = #{content}
where
	commu_no = #{commuNo}

update comu set
	viewyn = #{viewYN}
where commu_no = #{commuNo}
