mapper.select:
select * from SMI inner join image 
	img on smi.image_no = img.image_no
where SOCIAL_NO = #{SOCIAL_NO}

select * from ( select rownum rn, TMP.* from ( select s.social_no ,s.smalltype ,s.title, TO_CHAR(s.startdate, 'MM/DD HH24:mm') as startDate, TO_CHAR(s.enddate, 'MM/DD HH24:mm') as endDate, m.member_no,m.nick, mei.profileImgNo , map.detailaddress , smc.bigtype from social s inner join member m on s.member_no=m.member_no inner join (select member_no , min(image_no) as profileImgNo from mei group by member_no) mei on mei.member_no = s.member_no inner join map on map.map_no = s.map_no inner join smc on s.smalltype = smc.SMALLTYPE inner join sbc on smc.bigtype = sbc.BIGTYPE inner join (select social_no , min(image_no) image_no from smi group by social_no) smi on s.social_no = smi.social_no where s.hiddenyn ='N'
and ${categoryType} = #{category}
and #{keyword} = #{searchword}
and s.ENDDate < sysdate
and s.ENDDate > sysdate
and instr(map.DETAILADDRESS ,#{goo} ) > 0
order by s.social_no desc )TMP ) where rn between #{startRow} and #{endRow}

select * from (
    select rownum rn, TMP.* from (
        SELECT * FROM social WHERE hiddenyn = 'N' ORDER BY DBMS_RANDOM.RANDOM
    )TMP
) where rn between #{startRow} and #{endRow}
